#[test]
fn transaction_creation() {
	let transaction: serde_json::Value = serde_json::from_str(
		&init_governance_transaction(
			MULTI_SIG_POLICY,
			VERSION_ORACLE_VALIDATOR,
			VERSION_ORACLE_POLICY,
			governance_authority(),
			&tx_context(),
			genesis_utxo(),
			ExUnits::new(&789754u64.into(), &171220003u64.into()),
		)
		.unwrap()
		.to_json()
		.unwrap(),
	)
	.unwrap();

	assert_eq!(transaction, expected_transaction())
}

fn genesis_utxo() -> OgmiosUtxo {
	OgmiosUtxo {
		transaction: OgmiosTx {
			id: hex!("992a24e743a522eb3adf0bc39820a9a52093525f91ed6205b72fd4087c13b4ac"),
		},
		index: 1,
		value: OgmiosValue::new_lovelace(10000),
		address: test_address().to_bech32(None).unwrap(),

		..Default::default()
	}
}

fn payment_key() -> PrivateKey {
	PrivateKey::from_normal_bytes(&hex!(
		"94f7531c9639654b77fa7e10650702b6937e05cd868f419f54bcb8368e413f04"
	))
	.unwrap()
}

fn tx_context() -> TransactionContext {
	TransactionContext {
		payment_key: payment_key(),
		payment_utxos: vec![payment_utxo()],
		network: NetworkIdKind::Testnet,
		protocol_parameters: protocol_parameters(),
	}
}

fn governance_authority() -> MainchainAddressHash {
	MainchainAddressHash(hex!("76da17b2e3371ab7ca88ce0500441149f03cc5091009f99c99c080d9"))
}

fn payment_utxo() -> OgmiosUtxo {
	OgmiosUtxo {
		transaction: OgmiosTx {
			id: hex!("23249849e52ee17143509baf7a5abcbd76f9b589947d73e7bbc03ca9142f9535"),
		},
		index: 1,
		value: OgmiosValue::new_lovelace(9922945937),
		address: test_address().to_bech32(None).unwrap(),

		..OgmiosUtxo::default()
	}
}

fn test_address() -> Address {
	Address::from_bech32("addr_test1vpmd59ajuvm34d723r8q2qzyz9ylq0x9pygqn7vun8qgpkgs7y5hw").unwrap()
}

